export const initialState = {
    basket : [],
};

//selector

export const getBasketTotal = (basket) => basket?.reducer((amout , item) => item.price + amout,0);




const reducer = (state , action) => {

    switch(action.type){
        case 'ADD_TO_BASKET':
            return {
                ...state , basket : [...state.basket , action.item]
            };

        default:
            return state;   
    }
}

export default reducer;
