import { domain } from 'process';
import React from 'react';
import './Login.css';
import { Link } from 'react-router-dom';
import { useStateValue } from './StateProvider';
import {auth} from './firebase';



function Login(){

    const [email , setEmail] = useStateValue();
    const [password , setPassword] = useStateValue();

    const SignIn = e  => {
        e.preventDefault();


    }

    const Register = e => {
        e.preventDefault();

        auth.createUserwithEmailAndPassword(email , password);
            auth.then((auth) => {
                console.log(auth);
            })
            auth.catch(error => alert(error.message))


    }

    return(
        <div className = 'Login'>
        <Link to = '/'>
            <img className='login__logo' src='https://pngimg.com/uploads/amazon/amazon_PNG21.png' alt=""/>
        </Link>
            <div className = 'login__container'>
                <h1>Sign -in</h1>
                <form>
                    <h5>E-mail</h5>
                    <input type = 'text' value = {email} onChange = { e => setEmail(e.target.value)}/>

                    <h5>Password</h5>
                    <input type = 'password' value = {password} onChange = {e => setPassword(e.target.value)}/>

                    <button type='submit' onClick = {SignIn} className = 'login__SignInButton'>
                        Sign In
                    </button>
                </form>
                <p>
                    By signing - in you agree to the amazon clone fake conditions of use & sale.
                    Please notice our Privacy terms , Cookies terms & Interest based ads.
                </p>
                <button onClick={Register} className = 'login__RegisterButton'>
                    Create your Amazon Account
                </button>
            </div>
        </div>

    )
}
export default Login;
